<template>
    <div>
        <vue-avatar
                :width="400"
                :height="400"
                placeholder="data:image/png;base64,"
                :rotation="rotation"
                :borderRadius="borderRadius"
                :scale="scale"
                ref="vueavatar"
                @image-ready="onImageReady"
        >
        </vue-avatar>
        <br>
        <label>
            Zoom : {{scale}}x
            <br>
            <input
                    type="range"
                    min=1
                    max=3
                    step=0.02
                    v-model.number='scale'
            />
        </label>
        <br>
        <label>
            Rotation : {{rotation}}Â°
            <br>
            <input
                    type="range"
                    min=0
                    max=360
                    step=1
                    v-model.number='rotation'
            />
        </label>
        <br>
        <label>
            Radius : {{borderRadius}}px
            <br>
            <input
                    type="range"
                    min=0
                    max=200
                    step=1
                    v-model.number='borderRadius'
            />
        </label>
        <br>
        <button @click="saveClicked">Get image</button>
        <br>
        <img ref="image">
    </div>
</template>
<script>
    import VueAvatar from '@/components/VueAvatar';

    export default {
        components: {
            VueAvatar
        },
        data () {
            return {
                rotation: 0,
                scale: 1,
                borderRadius: 0
            };
        },
        methods: {
            saveClicked () {
                this.$refs.image.src = this.$refs.vueavatar.toDataUrl();
            },
            onImageReady () {
                this.scale = 1;
                this.rotation = 0;
            }
        }
    };
</script>
